import React from 'react';
import { View, Text, Image, StyleSheet } from 'react-native';

const Card = () => {
  return (
    <View style={styles.card}>
      <Image source={require('./cafe_image.png')} style={styles.image} />
      <Text style={styles.cardText}>¡Disfruta de nuestro delicioso café!</Text>
    </View>
  );
};

const styles = StyleSheet.create({
  card: {
    backgroundColor: '#fff',
    borderRadius: 8,
    padding: 16,
    alignItems: 'center',
  },
  image: {
    width: 200,
    height: 200,
    marginBottom: 10,
  },
  cardText: {
    fontSize: 18,
    textAlign: 'center',
  },
});

export default Card;
